mapper.select:
select gxky0201.id as id, gxky0201.xm as nickname, gxky0201.dlxtzh as account, gxky0201.ssjgbh as departmentCode, s_unit.NAME as departmentTitle from gxky0201 left join s_unit on s_unit.id = gxky0201.ssjgbh
ryscbz = 0 and gxky0201.dlxtzh = #{account}
limit 1

select gxjg0101.gh as id, gxjg0101.xm as nickname, gxjg0101.gh as account, #{year} as year, department.title as department from gxjg0101 left join department on department.id = gxjg0101.dwh
dqztm = 1 and type != 3 and dwh is not null and gxjg0101.xm like concat('%', #{nickname}, '%') and gxjg0101.gh like concat('%', #{account}, '%') and department.title like concat('%', #{department}, '%') and gxjg0101.dwh = #{departmentId}
order by gxjg0101.dwh asc
