mapper.select:
select gxjg0101.gh as id, #{year} as year, gxjg0101.xm as nickname, gxjg0101.gh as account, overtime_worked_subsidy.professional_position as professionalPosition, overtime_worked_subsidy.teaching_workload as teachingWorkload, workload_target.teaching_workload_target, IF( overtime_worked_subsidy.teaching_workload - workload_target.teaching_workload_target >= 0, (overtime_worked_subsidy.teaching_workload - workload_target.teaching_workload_target) * class_fees_standard.factor, 0 ) as overtimeSubsidy from gxjg0101 left join overtime_worked_subsidy on gxjg0101.gh = overtime_worked_subsidy.account and overtime_worked_subsidy.year = #{year} join department on department.id = gxjg0101.dwh left join performance_wage on performance_wage.account = gxjg0101.gh and performance_wage.year = #{year} left join workload_target on workload_target.department = gxjg0101.dwh and workload_target.year = #{year} and workload_target.type = performance_wage.post_type and workload_target.level = performance_wage.level left join class_fees_standard on class_fees_standard.year = #{year} and class_fees_standard.technical_posts_level = overtime_worked_subsidy.professional_position
and gxjg0101.xm like concat('%', #{nickname}, '%') and gxjg0101.gh like concat('%', #{account}, '%') and department.title like concat('%', #{department}, '%') and gxjg0101.dwh = #{departmentId}
and gxjg0101.dwh in
#{item}

select
    IF(
    overtime_worked_subsidy.teaching_workload - workload_target.teaching_workload_target >= 0,
    (overtime_worked_subsidy.teaching_workload - workload_target.teaching_workload_target) * class_fees_standard.factor,
    0
    ) as overtimeSubsidy
from overtime_worked_subsidy
join gxjg0101 on gxjg0101.gh = overtime_worked_subsidy.account
join performance_wage on performance_wage.account = gxjg0101.gh and performance_wage.year = #{year}
join workload_target on workload_target.department = gxjg0101.dwh and workload_target.year = #{year}
and workload_target.type = performance_wage.post_type and workload_target.level = performance_wage.level
join class_fees_standard on class_fees_standard.year = #{year} and class_fees_standard.technical_posts_level = overtime_worked_subsidy.professional_position

    overtime_worked_subsidy.year = #{year}
    and gxjg0101.gh = #{account}
