mapper.select:
select id, year, type, post_type as postType, alias, rule from salary_rule
year = #{year}
ORDER BY FIND_IN_SET(`type`,'教师,管理,辅导员,实验员')

select id, year, type, post_type as postType, alias, rule from salary_rule
year = #{year} and type = #{type} and post_type = #{postType}
limit 1

